package com.tmwsoft.sns.util;import java.util.HashMap;import java.util.Map;import javax.servlet.http.HttpServletRequest;public final class SysConstants {	public static final boolean IN_SNS = true;	public static final String SNS_CHARSET = "UTF-8";	public static final String SNS_VERSION = "1.0";	public static final int SNS_RELEASE = 20120815;	public static String snsRoot = null;	public static Map<String, String> snsConfig = new HashMap<String, String>();	public static Map<String, String> snsInfo = new HashMap<String, String>();	public static void setJchRoot(HttpServletRequest request) {		snsRoot = request.getSession().getServletContext().getRealPath("/");		if (snsRoot == null) {			try {				snsRoot = request.getSession().getServletContext().getResource("/").getPath();			} catch (Exception e) {				e.printStackTrace();			}		}		if (!snsRoot.endsWith("/"))			snsRoot = snsRoot + "/";	}}