package com.tmwsoft.sns.web.filter;import java.io.IOException;import java.io.PrintWriter;import javax.servlet.Filter;import javax.servlet.FilterChain;import javax.servlet.FilterConfig;import javax.servlet.ServletException;import javax.servlet.ServletRequest;import javax.servlet.ServletResponse;import javax.servlet.http.HttpServletResponse;public class AccessFilter implements Filter {	public void destroy() {	}	public void doFilter(ServletRequest req, ServletResponse res, FilterChain chain) throws IOException,			ServletException {		HttpServletResponse response = (HttpServletResponse)res;		response.setStatus(403);		response.sendError(403);		res.setCharacterEncoding("UTF-8");		PrintWriter out = res.getWriter();		out.write("非法访问！");		out.flush();		out.close();	}	public void init(FilterConfig arg0) throws ServletException {	}}